contracts/CampaignManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { Console } from "@fhenixprotocol/contracts/utils/debug/Console.sol";
import "@fhenixprotocol/contracts/FHE.sol";
import "./FHERC721/FhunderNFT.sol";
import { IFHERC20 } from "./FHERC20/IFHERC20.sol";

interface IFhunderNFT {
    function mintNFT(address recipient, euint128 encryptedAmount, uint256 campaignId, string memory tokenURICID) external returns (uint256);
    function balanceOf(address owner) external view returns (uint256);
}

contract CampaignManager {
    // Campaign structure
    struct Campaign {
        address creator;
        uint256 campaignId;
        string name;
        string description;
        uint128 minimumContribution;
        uint256 deadline;
        euint128 goal;
        euint128 totalContributions;
        euint32 numContributors;
        euint32 contributonsCount;
        // bool goalAchieved; // True if the goal is achieved before the deadline and funds can be released to the creator
        mapping(address => euint128) contributions;
        uint256 withdrawnDate;
    }

    // Mapping of campaign ID to campaign
    mapping(uint256 => Campaign) public campaigns;

    // Campaign counter to assign unique IDs
    uint256 public campaignCounter;

    address nftAddress;
    address tokenContractAddress;

    // Events
    event CampaignCreated(
        uint256 indexed campaignId,
        address indexed creator,
        string name,
        string description,
        euint128 goal,
        uint128 minimumContribution,
        uint256 deadline
    );

    event ContributionMade(
        uint256 indexed campaignId,
        address indexed contributor,
        euint128 amount,
        uint256 tokenId
    );

    event Withdrawal(
        uint256 indexed campaignId,
        address indexed contributor,
        euint128 amount
    );

    error CampaignDoesNotExist();
    error OnlyCampaignCreator();
    error InvalidContribution();
    error FundingGoalNotMet();

    // Constructor to set the NFT contract address
    constructor(address _fhunderNFTAddress, address _tokenContractAddress) {
        Console.log("[Init] Setting NFT contract to ", _fhunderNFTAddress);
        nftAddress = _fhunderNFTAddress;
        tokenContractAddress = _tokenContractAddress;
    }

    // Modifier to check if a campaign exists
    modifier campaignExists(uint256 campaignId) {
        if (campaigns[campaignId].creator == address(0)) revert CampaignDoesNotExist();
        // require(campaigns[campaignId].creator != address(0), "Campaign does not exist");
        _;
    }

    // Modifier to check if the caller is the campaign creator
    modifier onlyCampaignCreator(uint256 campaignId) {
        if (campaigns[campaignId].creator != msg.sender) revert OnlyCampaignCreator();
        // require(campaigns[campaignId].creator == msg.sender, "Only the campaign creator can call this function");
        _;
    }

    // Create a new campaign
    function createCampaign(
        string memory name,
        string memory description,
        inEuint128 calldata goal,
        uint128 minimumContribution,
        uint256 duration
    ) public {
        campaignCounter++;
        uint256 newCampaignId = campaignCounter;

        Campaign storage newCampaign = campaigns[newCampaignId];
        newCampaign.creator = msg.sender;
        newCampaign.campaignId = newCampaignId;
        newCampaign.name = name;
        newCampaign.description = description;
        newCampaign.goal = FHE.asEuint128(goal);
        newCampaign.minimumContribution = minimumContribution;
        newCampaign.deadline = block.timestamp + duration;
        newCampaign.totalContributions = FHE.asEuint128(0);
        newCampaign.numContributors = FHE.asEuint32(0);
        newCampaign.contributonsCount = FHE.asEuint32(0);

        Console.log("Campaign created with ID:", newCampaignId);
        emit CampaignCreated(newCampaignId, msg.sender, name, description, newCampaign.goal, minimumContribution, newCampaign.deadline);
    }

    // Make a contribution to a campaign
    function contribute(uint256 campaignId, inEuint128 calldata amount) public campaignExists(campaignId) {
        Campaign storage campaign = campaigns[campaignId];
        Console.log("Contributing to campaign", campaignId);
        Console.log("Campaign deadline: ", campaign.deadline);
        Console.log("Current block timestamp: ", block.timestamp);
        Console.log("Encrypted amount: ", uint128(FHE.decrypt(FHE.asEuint128(amount))));
        Console.log("Minimum contribution: ", campaign.minimumContribution);

        // Validations & Transfers
        // require(campaign.deadline > block.timestamp, "Campaign ended already");
        euint128 encryptedAmount = FHE.asEuint128(amount);

        ebool isValid = FHE.gte(FHE.asEuint128(uint128(FHE.decrypt(encryptedAmount))), FHE.asEuint128(campaign.minimumContribution));
        Console.log("Is valid: ", FHE.decrypt(isValid));
        // FHE.req(isValid);    
        Console.log("Transferring...");
        euint128 transferred = IFHERC20(tokenContractAddress)._transferFromEncrypted(msg.sender, address(this), FHE.asEuint128(amount));
        Console.log("Transferred: ", uint128(FHE.decrypt(transferred)));
        require(FHE.decrypt(transferred) > 0, "Transfer failed");
        Console.log("Transferred successfully");

        // // Update the campaign's state
        ebool isNewContributor = FHE.eq(campaign.contributions[msg.sender], FHE.asEuint128(0));
        campaign.totalContributions = FHE.add(campaign.totalContributions, encryptedAmount);
        campaign.contributonsCount = FHE.add(campaign.contributonsCount, FHE.asEuint32(1));
        campaign.numContributors = FHE.add(campaign.numContributors, FHE.select(isNewContributor, FHE.asEuint32(1), FHE.asEuint32(0)));
        campaign.contributions[msg.sender] = FHE.add(campaign.contributions[msg.sender], FHE.asEuint128(amount));

        // // Mint NFT 
        // string memory tokenURICID = "QmdLi5N4SZGsKoRbKta2P1EAcp6KzWcgPnB4UCHwho1UZ8";
        // uint256 nftTokenId = FhunderNFT(nftAddress).mintNFT(msg.sender, encryptedAmount, campaignId, tokenURICID);

        // emit ContributionMade(campaignId, msg.sender, encryptedAmount, nftTokenId);
        emit ContributionMade(campaignId, msg.sender, FHE.asEuint128(amount), 0);
    }

    function withdraw(uint256 campaignId) public onlyCampaignCreator(campaignId) {
        Campaign storage campaign = campaigns[campaignId];

        // Validations
        FHE.req(FHE.gte(campaign.totalContributions, campaign.goal));
        require(block.timestamp >= campaign.deadline, "Campaign is not yet ended");

        // Transfer tokens to the creator
        IFHERC20(tokenContractAddress)._transferEncrypted(msg.sender, campaign.totalContributions);

        // Reset the campaign's state
        campaign.withdrawnDate = block.timestamp;

        // Events
        emit Withdrawal(campaignId, msg.sender, campaign.totalContributions);
    }

    function releaseFunds(uint256 campaignId) public campaignExists(campaignId) {
        Campaign storage campaign = campaigns[campaignId];

        // Check if the funding goal is not met
        // FHE.req(FHE.lte(campaign.totalContributions, campaign.goal), "Funding goal met, cannot release funds");

        // Get the contributor's contribution
        euint128 contributorAmount = campaign.contributions[msg.sender];
        // FHE.req(FHE.lte(contributorAmount, FHE.asEuint128(0)), "Nothing to release..");

        // Reset the contributor's contribution
        campaign.contributions[msg.sender] = FHE.asEuint128(0);

        // Transfer tokens back to the contributor
        // IFHERC20(tokenContractAddress)._transferEncrypted(address(this), msg.sender, FHE.asEuint128(campaign.contributions[msg.sender]));

        emit Withdrawal(campaignId, msg.sender, campaign.contributions[msg.sender]);
    }

    // Get campaign details
    function getCampaign(uint256 campaignId) 
      public 
      view 
      campaignExists(campaignId)
      returns (
        address creator,
        string memory name,
        string memory description,
        uint128 goal,
        uint128 minimumContribution,
        uint256 deadline,
        uint128 totalContributions,
        uint32 numContributors,
        uint32 contributonsCount,
        uint256 withdrawnDate
    ) {
        Campaign storage campaign = campaigns[campaignId];

        return (
            campaign.creator,
            campaign.name,
            campaign.description,
            FHE.decrypt(campaign.goal),
            campaign.minimumContribution,
            campaign.deadline,
            FHE.decrypt(campaign.totalContributions),
            FHE.decrypt(campaign.numContributors),
            FHE.decrypt(campaign.contributonsCount),
            campaign.withdrawnDate
        );
    }

    // Decrypt and get the total contributions (only for campaign creator)
    function decryptTotalContributions(uint256 campaignId) 
      public 
      view 
      campaignExists(campaignId)
      onlyCampaignCreator(campaignId)
      returns (uint128) {
        return FHE.decrypt(campaigns[campaignId].totalContributions);
    }

    // Update campaign description
    function updateCampaignDescription(uint256 campaignId, string memory newDescription) 
      public
      campaignExists(campaignId)
      onlyCampaignCreator(campaignId) {
        Campaign storage campaign = campaigns[campaignId];
        campaign.description = newDescription;
    }

    function getTime() public view returns (uint256) {
        return block.timestamp;
    }
}

contracts/FHERC20/FhunderToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.19 <0.9.0;

import { Console } from "@fhenixprotocol/contracts/utils/debug/Console.sol";
import { ERC20 } from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

import { FHE, euint128, inEuint128 } from "@fhenixprotocol/contracts/FHE.sol";
import { IFHERC20 } from "./IFHERC20.sol";

import "@fhenixprotocol/contracts/access/Permissioned.sol";

error ErrorInsufficientFunds();
error ERC20InvalidApprover(address);
error ERC20InvalidSpender(address);

contract FhunderToken is IFHERC20, ERC20, Permissioned, Ownable, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    // A mapping from address to an encrypted balance.
    mapping(address => euint128) internal _encBalances;

    // A mapping from address (owner) to a mapping of address (spender) to an encrypted amount.
    mapping(address => mapping(address => euint128)) internal _allowed;

    euint128 internal totalEncryptedSupply = FHE.asEuint128(0);

    uint8 private _customDecimals;

    event FhunderTransfer(address indexed from, address indexed to, uint256 amount);
    event FhunderMint(address indexed to, uint256 amount);
    event FhunderEMint(address indexed to, euint128 amount);

    constructor(string memory name, string memory symbol, uint8 decimals_, address owner_) ERC20(name, symbol) Ownable(owner_) {
        _customDecimals = decimals_;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(BURNER_ROLE, msg.sender);
    }

    function decimals() public view virtual override returns (uint8) {
        return _customDecimals;
    }

    function _allowanceEncrypted(address owner, address spender) internal view returns (euint128) {
        return _allowed[owner][spender];
    }

    function allowanceEncrypted(
        address owner,
        address spender,
        Permission calldata permission
    ) public view virtual onlyPermitted(permission, owner) returns (string memory) {
        return FHE.sealoutput(_allowanceEncrypted(owner, spender), permission.publicKey);
    }

    function approveEncrypted(address spender, inEuint128 calldata value) public virtual returns (bool) {
        _approve(msg.sender, spender, FHE.asEuint128(value));
        return true;
    }

    function _approve(address owner, address spender, euint128 value) internal {
        if (owner == address(0)) {
            revert ERC20InvalidApprover(address(0));
        }
        if (spender == address(0)) {
            revert ERC20InvalidSpender(address(0));
        }
        _allowed[owner][spender] = value;
        Console.log("[_approve] Owner: ", owner);
        Console.log("[_approve] Spender: ", spender);
        Console.log("[_approve] Value: ", uint128(FHE.decrypt(_allowed[owner][spender])));
    }

    function _spendAllowance(address owner, address spender, euint128 value) internal virtual returns (euint128) {
        Console.log("[_spendAllowance] Owner: ", owner);
        Console.log("[_spendAllowance] Spender: ", spender);
        Console.log("[_spendAllowance] Value: ", uint128(FHE.decrypt(value)));
        euint128 currentAllowance = _allowanceEncrypted(owner, spender);
        Console.log("[_spendAllowance] Current allowance: ", uint128(FHE.decrypt(currentAllowance)));
        euint128 spent = FHE.min(currentAllowance, value);
        Console.log("[_spendAllowance] Spent: ", uint128(FHE.decrypt(spent)));
        _approve(owner, spender, FHE.sub(currentAllowance, spent));

        return spent;
    }

    function transferFromEncrypted(address from, address to, inEuint128 calldata value) public virtual returns (euint128) {
        return _transferFromEncrypted(from, to, FHE.asEuint128(value));
    }

    function _transferFromEncrypted(address from, address to, euint128 value) public virtual returns (euint128) {
        Console.log("[_transferFromEncrypted] Transferring from: ", from);
        Console.log("[_transferFromEncrypted] To: ", to);
        Console.log("[_transferFromEncrypted] Value: ", uint128(FHE.decrypt(value)));
        euint128 spent = _spendAllowance(from, msg.sender, value);
        Console.log("[_transferFromEncrypted] Spent: ", uint128(FHE.decrypt(spent)));
        return _transferImpl(from, to, spent);
    }

    function wrap(uint128 amount) public {
        if (balanceOf(msg.sender) < amount) {
            revert ErrorInsufficientFunds();
        }

        _burn(msg.sender, amount);
        euint128 eAmount = FHE.asEuint128(amount);
        _encBalances[msg.sender] = FHE.add(_encBalances[msg.sender], eAmount);
        totalEncryptedSupply = FHE.add(totalEncryptedSupply, eAmount);
    }

    function unwrap(uint128 amount) public {
        euint128 encAmount = FHE.asEuint128(amount);

        euint128 amountToUnwrap = FHE.select(FHE.gte(_encBalances[msg.sender], encAmount), encAmount, FHE.asEuint128(0));

        _encBalances[msg.sender] = FHE.sub(_encBalances[msg.sender], amountToUnwrap);
        totalEncryptedSupply = FHE.sub(totalEncryptedSupply, amountToUnwrap);

        _mint(msg.sender, FHE.decrypt(amountToUnwrap));
    }

    function mint(uint256 amount) public onlyRole(MINTER_ROLE) {
        emit FhunderMint(msg.sender, amount);
        _mint(msg.sender, amount);
    }

    function mintEncrypted(address to, inEuint128 calldata encryptedAmount) public onlyRole(MINTER_ROLE) {
        emit FhunderEMint(msg.sender, FHE.asEuint128(encryptedAmount));
        _mintEncrypted(to, FHE.asEuint128(encryptedAmount));
    }

    function _mintEncrypted(address to, euint128 amount) internal {
        _encBalances[to] = FHE.add(_encBalances[to], amount);
        totalEncryptedSupply = FHE.add(totalEncryptedSupply, amount);
    }

    function burnFromEncrypted(address account, inEuint128 calldata encryptedAmount) public onlyRole(BURNER_ROLE) {
        _burnFromEncrypted(account, FHE.asEuint128(encryptedAmount));
    }

    function _burnFromEncrypted(address account, euint128 amount) internal {
        euint128 amountToBurn = FHE.select(_encBalances[account].gte(amount), amount, FHE.asEuint128(0));

        _encBalances[account] = FHE.sub(_encBalances[account], amountToBurn);
        totalEncryptedSupply = FHE.sub(totalEncryptedSupply, amountToBurn);
    }

    function transferEncrypted(address to, inEuint128 calldata encryptedAmount) public returns (euint128) {
        return _transferEncrypted(to, FHE.asEuint128(encryptedAmount));
    }

    // Transfers an amount from the message sender address to the `to` address.
    function _transferEncrypted(address to, euint128 amount) public returns (euint128) {
        emit FhunderTransfer(msg.sender, to, FHE.decrypt(amount));
        return _transferImpl(msg.sender, to, amount);
    }

    // Transfers an encrypted amount.
    function _transferImpl(address from, address to, euint128 amount) internal returns (euint128) {
        Console.log("[_transferImpl] Transferring from: ", from);
        Console.log("[_transferImpl] To: ", to);
        Console.log("[_transferImpl] Value: ", uint128(FHE.decrypt(amount)));
        Console.log("[_transferImpl] Encrypted balance of from: ", uint128(FHE.decrypt(_encBalances[from])));
        // Make sure the sender has enough tokens.
        euint128 validAmount = FHE.asEuint128(uint128(FHE.decrypt(amount)));
        Console.log("[_transferImpl] Is lte : ", FHE.decrypt(FHE.lte(validAmount, _encBalances[from])));

        euint128 amountToSend = FHE.select(FHE.lte(validAmount, _encBalances[from]), validAmount, FHE.asEuint128(0));
        Console.log("[_transferImpl] Amount to send: ", uint128(FHE.decrypt(amountToSend)));

        _encBalances[to] = FHE.add(_encBalances[to], amountToSend);
        _encBalances[from] = FHE.sub(_encBalances[from], amountToSend);

        return amountToSend;
    }

    function balanceOfEncrypted(address account, Permission memory auth) public view virtual onlyPermitted(auth, account) returns (string memory) {
        return _encBalances[account].seal(auth.publicKey);
    }

    function balanceOfEncrypted() external view virtual returns (euint128) {
        return _encBalances[msg.sender];
    }

    function decryptedBalanceOf() public view virtual returns (uint256) {
        return FHE.decrypt(_encBalances[msg.sender]);
    }

    function decryptedBalanceOf(address account) public view virtual returns (uint256) {
        return FHE.decrypt(_encBalances[account]);
    }
}

